package hongke.interview.leetcode.questions;

import java.util.*;

/**
 * Created by hongke on 4/18/14.
 */
public class DistinctSubsequences {

    Map<List<Integer>, Integer> cache;

    public int numDistinct1(String S, String T) {
        if (S == null || T == null || S.length() == 0 || T.length() == 0) {
            return 0;
        }

        Integer[][] result = buildCache(S.toCharArray(), T.toCharArray());
        return result[S.length() - 1][T.length() - 1];
    }

    // The way how to build cache is initially proposed
    private Integer[][] buildCache(char[] s, char[] t) {
        Integer[][] cache = new Integer[s.length][t.length];
        // initialization
        for (int i = 0; i < t.length; i++) {
            cache[0][i] = 0;
        }
        int repeat = 0;
        for (int i = 0; i < s.length; i++) {
            if (s[i] == t[0]) {
                repeat++;
            }
            cache[i][0] = repeat;
        }
        for (int i = 1; i < t.length; i++) {
            for (int j = 1; j < s.length; j++) {
                if (s[j] == t[i]) {
                    cache[j][i] = cache[j - 1][i - 1] + cache[j - 1][i];
                } else {
                    cache[j][i] = cache[j - 1][i];
                }
            }
        }
        return cache;
    }


    public int numDistinct(String S, String T) {
        if (S == null || S.length() == 0 || T == null || T.length() == 0) {
            return 0;
        }
        int[][] state = new int[T.length() + 1][S.length() + 1];
        Arrays.fill(state[0], 1);
        char[] s = S.toCharArray(), t = T.toCharArray();
        for (int i = 1; i <= t.length; i ++) {
            for (int j = i; j <= s.length; j ++) {
                state[i][j] = state[i][j - 1]; // copy # of matches already calculated
                if (t[i - 1] == s[j - 1])
                    state[i][j] += state[i - 1][j - 1]; // plus # of matches ended at current position
            }
        }
        return state[T.length()][S.length()];
    }

    public static void main(String[] args) {
        DistinctSubsequences test = new DistinctSubsequences();

        System.out.println(test.numDistinct("ccc", "c"));
        System.out.println(test.numDistinct(
                "", "rwmimatmhydhbujebqehjprarwfkoebcxxqfktayaaeheys"));
        System.out.println(test.numDistinct("", "AC"));
        System.out.println(test.numDistinct("AB", ""));
        System.out.println(test.numDistinct(null, null));
        System.out.println(test.numDistinct("b", "a"));
        System.out.println(test.numDistinct("AB", "AD"));
        System.out.println(test.numDistinct("ABCC", "AC"));
        System.out.println(test.numDistinct("rabbbit", "rabbit"));
        System.out.println(test.numDistinct("aaa", "aa"));
    }

}
